import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Popup, Marker } from "react-leaflet";
import { Link } from "react-router-dom";

import { ROUTES } from "../../constants/routes";

import styles from "./styles.module.scss";

const NearbyRestaurants = () => {
	const [restaurantsOne, setRestaurantsOne] = useState([]);
	const [restaurantsTwo, setRestaurantsTwo] = useState([]);
	const [restaurants, setRestaurants] = useState([]);

	useEffect(() => {
		fetch("https://ver-la-carta.herokuapp.com/restaurantes/")
			.then((res) => res.json())
			.then((json) => {
				if (json.status === 404) {
					console.log("error");
				} else {
					setRestaurantsOne(json);
				}
			});
		fetch(
			"https://virtserver.swaggerhub.com/adrian-castiglione/arquiweb-tp1/1.0.0/api/restaurants"
		)
			.then((res) => res.json())
			.then((json) => {
				setRestaurantsTwo(json.items);
			});
	}, []);

	useEffect(() => {
		console.log(restaurantsOne, restaurantsTwo)
	}, [restaurantsOne, restaurantsTwo]);

	return (
		<div className={styles.mapContainer}>
			<div className={styles.title}>
				<h1> Restaurantes cercanos </h1>
				<Link to={ROUTES.HOME}>
					<button className={styles.button}> Volver </button>
				</Link>
			</div>
			<MapContainer
				className={styles.map}
				center={[-34.603722, -58.381592]}
				zoom={12}
				scrollWheelZoom
			>
				<TileLayer
					url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
					attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
				/>
				{restaurants.map((restaurant) => (
					<Marker
						position={[
							parseFloat(restaurant.latitude),
							parseFloat(restaurant.longitude),
						]}
					>
						<Popup>
							<div className={styles.pin}>
								<span className={styles.label}> {restaurant.name} </span>
								<span className={styles.label}> {restaurant.phoneNumber} </span>
								<span className={styles.label}>{restaurant.workingHours} </span>
							</div>
						</Popup>
					</Marker>
				))}
			</MapContainer>
		</div>
	);
};

export default NearbyRestaurants;
